IF OBJECT_ID('GetAllMenuItem') IS NOT NULL
BEGIN--IF
	DROP PROCEDURE [GetAllMenuItem]
END--IF
GO

CREATE PROCEDURE [GetAllMenuItem] AS
BEGIN--PROCEDURE
	SET NOCOUNT ON;

    /* Generated by AsapWiki-ADODAL-Procedures */
	
	
	SELECT
		[Id] as [Id],
		[Name] as [Name],
		[Link] as [Link],
		[Ordinal] as [Ordinal]
	FROM
		[MenuItem]

END--PROCEDURE
GO

