CREATE PROCEDURE [UpdateMenuItemById]
(
	@Id as int,
	@Name as nvarchar (128),
	@Link as nvarchar (128),
	@Ordinal as int
) AS
BEGIN--PROCEDURE
	SET NOCOUNT ON;

    /* Generated by AsapWiki-ADODAL-Procedures */
	
	UPDATE
		[MenuItem]
	SET
		[Name] = @Name,
		[Link] = @Link,
		[Ordinal] = @Ordinal
	FROM
		[MenuItem]
	WHERE
		Id = @Id

END--PROCEDURE
GO

